package algorithm_practice;/** * @Author:zhenyuan * @Date:Created in 21:00 2018/7/29 */public class Hanluota {    public static void f(int n,String src,String des,String aus){        if (n==1){            System.out.println("move 1 from "+src+" to "+des);            return;        }        f(n-1,src,aus,des);        System.out.println("move "+n+" from "+src+" to "+des);        f(n-1,aus,des,src);    }    public static void main(String[] args){        f(3,"源端","目的","辅助");    }}