package algorithm_practice;import zhen.yuan.MyPrint;import zhen.yuan.MyRandom;/** * 荷兰国旗问题 * @Author:zhenyuan * @Date:Created in 17:03 2018/7/22 */public class Practice_01 {    public static void main(String[] args){        int count =30;        while (count-->0){            int[] a = MyRandom.getRandomArray(10,20);            sort(a,10);            MyPrint.printArray(a,"result");            try {                Thread.sleep(2000);            } catch (InterruptedException e) {                e.printStackTrace();            }        }    }    public static void sort(int[] array,int aim){        int length = array.length;        int down=0,up=length-1,mid = 0;        for (int i=0;i<=up;i++){            if (aim>array[i]) {                swap(array,i,down);                down++;                continue;            }            if (aim==array[i]) continue;            swap(array,i,up);            up--;            i--;        }    }    static void swap(int [] array,int a,int b){        int temp = array[a];        array[a]=array[b];        array[b]=temp;    }}