package algorithm_practice;/** * @Author:zhenyuan * @Date:Created in 13:53 2018/7/29 */public class PrintStr {    public static void  print(String str,String cur,int i){        if (i==str.length()){            System.out.println(cur);            return;        }        print(str,cur,i+1);        print(str,cur+String.valueOf(str.charAt(i)),i+1);    }    public static void main(String[] args){        print("abc","",0);    }}